    printf("Input coordinate 2 : ");
    scanf("%f %f",&x2,&y2);

    printf("Input coordinate 3 : ");
    scanf("%f %f",&x3,&y3);
    a=(x1*(y2-y3)+x2*(y3-y1)+x3*(y1-y2))/2;
    if(a<0){
    printf("Area is %f\n",a*(-1));}
    else
    {
        printf("Area is %f\n",a);
    }
    // CALCULATING SIDE LENGTHS
    s1=sqrt(((x2-x1)*(x2-x1))+ ((y2-y1)*(y2-y1)));
    s2=sqrt(((x3-x2)*(x3-x2))+ ((y3-y2)*(y3-y2)));
    s3=sqrt(((x3-x1)*(x3-x1))+ ((y3-y1)*(y3-y1)));
    //CHECKING IF A TRIANGLE IS VALID OR NOT
    if (s1==0 || s2==0 || s3==0 || a==0)
    {
        printf("It is not a valid triangle");
    }
    else{
        // EQUILATERAL / ISOSCELES/ SCALENE CHECK
    if(s1==s2 && s2==s3)
    {
        printf("Equilateral Triangle\n");
    }
    else if(s1==s2 || s2==s3 || s3==s1)
    {
        printf("Isosceles Triangles\n");
    }
    else
    {
        printf("Scalene Triangle\n");
    }
    // ACUTE / OBTUSE/ RIGHT ANGLE CHECK
    x = ((x2-x1)*(x2-x1))+ ((y2-y1)*(y2-y1));
    y = ((x3-x2)*(x3-x2))+ ((y3-y2)*(y3-y2));
    z = ((x3-x1)*(x3-x1))+ ((y3-y1)*(y3-y1));
    if(s1>s2 && s1>s3) // LARGEST IS S1
    {
        if(x == y + z)
            printf( " Triangle is Right-angled");
        else if(x < y + z)
            printf( "Triangle is acute-angled");
        else
            printf ("Triangle is obtuse-angled");

    }

    else if(s2>s1 && s2>s3) // LARGEST IS S2
    {
        if(y == x + z)
            printf( "Triangle is right-angled");
        else if(y < x + z)
            printf ("Triangle is  acute-angled");
        else
            printf("Triangle is obtuse-angled");
    }
    else //LARGEST IS S3
    {
        if(z == x + y)
            printf( "Triangle is right-angled");
        else if(z < x + y)
            printf("Triangle is acute-angled");
        else
            printf("Triangle is obtuse-angled");
    }}

}

