float dist(int x1,int y1,int x2,int y2){
    float d=sqrt(pow((float)(x1-x2),2)+pow((float)(y1-y2),2));
    return d;
}

float area(int x1,int y1,int x2,int y2,int x3,int y3){
    float ar=0.50*(x1*(y2-y3)+x2*(y3-y1)+x3*(y1-y2));
    if (ar>=0){
        return ar;
    }
    return ar*(-1);
}

int main(){

    int x1,x2,x3,y1,y2,y3;

    printf("Enter the coordinate of the first point:\n");
    scanf("%d%d",&x1,&y1);
    printf("%d %d\n",x1,y1);

    printf("Enter the coordinate of the second point:\n");
    scanf("%d%d",&x2,&y2);
    printf("%d %d\n",x2,y2);

    printf("Enter the coordinate of the third point:\n");
    scanf("%d%d",&x3,&y3);
    printf("%d %d\n",x3,y3);

    if((x1==x2 && y1==y2) || (x2==x3 && y2==y3) || (x3==x1 && y3==y1)){
        printf("Non-distinct input point\n");
        float max;
        max=dist(x1,y1,x2,y2);
        if(max<dist(x2,y2,x3,y3)){
            max=dist(x2,y2,x3,y3);
        }
        if(max<dist(x3,y3,x1,y1)){
            max=dist(x3,y3,x1,y1);
        }
        if(max!=0){
            printf("Distance between distinct points: %.2f\n",max);
        }
    }
    else{
        float ar=area(x1,y1,x2,y2,x3,y3);

        if(ar==0){
            printf("Formed Straight Line\n");
            float max;
            max=dist(x1,y1,x2,y2);
            if(max<dist(x2,y2,x3,y3)){
                max=dist(x2,y2,x3,y3);
            }
            if(max<dist(x3,y3,x1,y1)){
                max=dist(x3,y3,x1,y1);
            }
            printf("Distance between end points: %.2f\n",max);
        }
        else{
            printf("Formed triangle \n Area of the triangle is %.2f\n",ar);
        }
    }

    return 0;
}

