//declaring the player structure with its members
struct Player {
    char name[100];
    char country[100];
    char dob[9];
    char date_debut[9];
    int n_tests;
    int n_odi;
    int n_cent;
    int n_half_cent;
    int tot_test;
    int tot_odi;
    int wicket_test;
    int wicket_odi;
    struct Player* next;
};

//function to take input new number of players in the list and modifying the list accordingly
struct Player* input(struct Player* r){
    int k;
    printf("Enter the new number of elements of the list: ");
    scanf("%d",&k);
    int n = 0; //n stores the number of elements in the original list
    struct Player* p = r;
    //calculating n
    while(p!=NULL){
        n++;
        p = p->next;
    }

    //checking if elements need to be added or deleted
    if(k>n){
        //if we need addition
        printf("Enter the new elements:\n");
        for(int i=0;i<k-n;i++){
            //new node
            struct Player* node = (struct Player*) malloc(sizeof(struct Player));
            printf("\n");
            //taking input new player details
            printf("Enter the name of the player : ");
            scanf("%s", node->name);
            printf("Enter the country of the player : ");
            scanf("%s", node->country);
            printf("Enter the date of birth of the player : ");
            scanf("%s", node->dob);
            printf("Enter the date of debut first class match of the player : ");
            scanf("%s", node->date_debut);
            printf("Enter the number of tests played by the player : ");
            scanf("%d", &(node->n_tests));
            printf("Enter the number of one day internationals (ODIs) played by the player : ");
            scanf("%d", &(node->n_odi));
            printf("Enter the number of centuries scored by the player : ");
            scanf("%d", &(node->n_cent));
            printf("Enter the number of half centuries scored the player : ");
            scanf("%d", &(node->n_half_cent));
            printf("Enter the total runs scored in tests by the player : ");
            scanf("%d", &(node->tot_test));
            printf("Enter the total runs made in ODIs by the player : ");
            scanf("%d", &(node->tot_odi));
            printf("Enter the number of wickets taken in test cricket by the player : ");
            scanf("%d", &(node->wicket_test));
            printf("Enter the number of wickets taken in ODIs by the player : ");
            scanf("%d", &(node->wicket_odi));
            if(r==NULL){
                //if this node is the first node
                r = node;
                r->next = NULL;
            }else{
                p = r;
                struct Player* prev;
                while(p!=NULL){

                    //changing upperCase to lowerCase for comparison
                    char name1[100];
                    char name2[100];
                    int i = 0;
                    while(p->name[i]!='\0'){
                        if(p->name[i] >='A' && p->name[i]<='Z'){
                            name1[i] = p->name[i]-'A'+'a';
                        }else{
                            name1[i] = p->name[i];
                        }
                        i++;
                    }
                    name1[i] = '\0';
                    i = 0;
                    while(node->name[i]!='\0'){
                        if(node->name[i] >='A' && node->name[i]<='Z'){
                            name2[i] = node->name[i]-'A'+'a';
                        }else{
                            name2[i] = node->name[i];
                        }
                        i++;
                    }
                    name2[i] = '\0';
                    if(strcmp(name1, name2)>0){
                        break;
                    }
                    prev = p;
                    p = p->next;
                }
                //new node needs to added between prev and p
                if(p==r){
                    //addition at start
                    r = node;
                    r->next = p;
                }else if(p==NULL){
                    //addition at end
                    prev->next = node;
                    node->next = NULL;
                }else{
                    //addition in between
                    prev->next = node;
                    node->next = p;
                }
            }
        }
    }else if(k<n){
        //deleting extra nodes at the end
        int counter = 0;
        struct Player* prev;
        p = r;
        while(counter!=k){
            prev = p;
            p = p->next;
            counter++;
        }
        while(prev->next!=NULL){
            prev->next = p->next;
            free(p);
            p = prev->next;
        }
        printf("Done deleting extra player records\n");
    }else{
        printf("The list already contains this many players\n");
    }
    return r;
}

//this function counts required ODIs more than or equal to n
int one_day_counter(int n, struct Player* node, int odi){
    if(node!=NULL){
        if(node->n_odi >= n){
            return(one_day_counter(n, node->next, odi+1)); //recursive call with odi+1
        }
        return(one_day_counter(n, node->next, odi));
    }
    return odi;
}

