int main()
{
    int n, n1 = 1, max, max1, min, min1;
    printf("Enter any positive integer numbers__\n");
    scanf("%d", &n);
    if (n < 0)
    {
        return 0;
    }
    printf("\nlargest number: %d\n second largest number: not enetred yet\n", n);
    printf("smallest number: %d\n second smallest number: not enetred yet\n\n**********\n", n);
    max = n;
    min = n;
    printf("Enter any positive integer numbers__\n");
    scanf("%d", &n1);
    while (1)
    {
        if (n1 < 0)
        {
            return 0; //closes the program for negative values
        }
        if (n1 > max)
        { //switches the variables
            max1 = max;
            max = n1;
        }
        else if (n1 < max && n1 > max1)
        { //switches the variables
            max1 = n1;
        }
        if (n1 < min)
        { //switches the variables
            min1 = min;
            min = n1;
        }
        else if (n1 > min && n1 < min1)
        { //switches the variables
            min1 = n1;
        }
        printf("\nlargest number: %d\n second largest number:%d\n", max, max1);
        printf("smallest number: %d\n second smallest number:%d \n\n**********\n", min, min1);
        printf("Enter any positive integer numbers__\n");
        scanf("%d", &n1); //enters new value for n1 for the next loop
    }
    return 0;
}

