int main(){
    float x,y,z;
    int n;

    // Part 1(a) where we take x,y,z,n as input from user
    printf("Enter the value of three floating point numbers x,y,z one by one side by side with a space between them: ");
    scanf("%f%f%f",&x,&y,&z);
    printf("Enter the value of n which is a positive integer: ");
    scanf("%d",&n);

    float a,b;

    // Part 1(b) where we take a as input from user till user gives a such that -1<a<1
    printf("Enter the value of a which should be such that -1<a<1: ");
    scanf("%f",&a);
    while(a<=-1 || a>=1){
        printf("Value of a is not in the specified range. Please enter the value of a once again: ");
        scanf("%f",&a);
    }

    // Part 1(c) where we take b as input from user till user gives b such that -0.5<b<0.5
    printf("Enter the value of b which should be such that -0.5<b<0.5: ");
    scanf("%f",&b);
    while(b<=-0.5 || b>=0.5){
        printf("Value of b is not in the specified range. Please enter the value of b once again: ");
        scanf("%f",&b);
    }

    float term=1.0,cosx=0.0,cosy=0.0,cosz=0.0,cosa=0.0,cosb=0.0; // term variable is used to find the value of each term of the cos series
    /* Here term variable is always positive since its multiplied by twice in each iteration
       we just add or subtract this positive value of term variable as required in the series
    */
    int fac=1; // fac variable is used to find the value of the factorial of 2,4,6,8,... as the series progresses

    /*Part 2(a)*/

    // Computing cos(x) upto n terms of the infinite series
    for(int i=1;i<=n;i++){
        if(i%2!=0){
            cosx+=term;
            term*=x/(fac);
            fac++;
            term*=x/(fac);
            fac++;
        }else{
            cosx-=term;
            term*=x/(fac);
            fac++;
            term*=x/(fac);
            fac++;
        }
    }

    // Resetting fac and term for reuse
    term=1.0;
    fac=1;

    // Computing cos(y) upto n terms of the infinite series
    for(int i=1;i<=n;i++){
        if(i%2!=0){
            cosy+=term;
            term*=y/(fac);
            fac++;
            term*=y/(fac);
            fac++;
        }else{
            cosy-=term;
            term*=y/(fac);
            fac++;
            term*=y/(fac);
            fac++;
        }
    }

    // Resetting fac and term for reuse
    term=1.0;
    fac=1;

    // Computing cos(z) upto n terms of the infinite series
    for(int i=1;i<=n;i++){
        if(i%2!=0){
            cosz+=term;
            term*=z/(fac);
            fac++;
            term*=z/(fac);
            fac++;
        }else{
            cosz-=term;
            term*=z/(fac);
            fac++;
            term*=z/(fac);
            fac++;
        }
    }

    // Resetting fac and term for reuse
    term=1.0;
    fac=1;
    int counter_a=0; // this variable is used to keep record of the number of terms used to find cos(a)

    /*Part 2(b)*/

    //Computing cos(a) using the infinite series, upto terms having magnitude 10^-5
    while(term>=pow(10,-5)){
        counter_a++;
        if(counter_a%2!=0){
            cosa+=term;
            term*=a/(fac);
            fac++;
            term*=a/(fac);
            fac++;
        }else{
            cosa-=term;
            term*=a/(fac);
            fac++;
            term*=a/(fac);
            fac++;
        }
    }

    // Resetting fac and term for reuse
    term=1.0;
    fac=1;
    int counter_b=0; // this variable is used to keep record of the number of terms used to find cos(b)

    //Computing cos(b) using the infinite series, upto terms having magnitude 10^-5
    while(term>=pow(10,-5)){
        counter_b++;
        if(counter_b%2!=0){
            cosb+=term;
            term*=b/(fac);
            fac++;
            term*=b/(fac);
            fac++;
        }else{
            cosb-=term;
            term*=b/(fac);
            fac++;
            term*=b/(fac);
            fac++;
        }
    }

    /*Part 3(a)*/
    //Printing cos(x), cos(y), cos(z) calculated upto n terms
    printf("The value of cos(x) calculated upto %d terms is: %f\n",n,cosx);
    printf("The value of cos(y) calculated upto %d terms is: %f\n",n,cosy);
    printf("The value of cos(z) calculated upto %d terms is: %f\n",n,cosz);

    /*Part 3(b)*/
    //Printing cos(a) value
    printf("The value of cos(a) calculated upto terms with magnitude 10^-5 is: %f\n",cosa);
    //Printing number of terms of the infinite series that had to be calculated to find cos(a)
    printf("The number of terms that had to be calculated to find cos(a) are: %d\n",counter_a);
    //Printing cos(b) value
    printf("The value of cos(b) calculated upto terms with magnitude 10^-5 is: %f\n",cosb);
    //Printing number of terms of the infinite series that had to be calculated to find cos(b)
    printf("The number of terms that had to be calculated to find cos(b) are: %d\n",counter_b);
    return 0;
}

#include<stdio.h>

int main(){

    /*Part I*/

    //Task 1
    int x,n,k,sum=0;
    printf("Write number x: ");
    scanf("%d",&x);
    k=x; //k is used to store value of x so that x is preserved for later comparison with sum
    while(k>0){
        int d=k%10; //d stores last digit of the number k
        int prod=1; //prod stores value of factorial of each digit
        for(int i=2;i<=d;i++){
            prod*=i;
        }
        sum+=prod;
        k/=10; //k is divided by 10 to make second last digit as last digit for furthur use in loop
    }
    if(sum==x){
        printf("%d is an IIT number\n",x);
    }else{
        printf("%d is not an IIT number\n",x);
    }

    //Task 2
    printf("Write number n: ");
    scanf("%d",&n);
    printf("The IIT numbers between 1 and %d: ",n);
    for(int i=1;i<=n;i++){
        int num=i; //num stores value of i so that i is not affected in the process as its reserved for the for-loop
        sum=0;
        while(num>0){
            int d=num%10; //this d has same use as that in Task 1
            int prod=1; //this prod has same use as in Task 1
            for(int i=2;i<=d;i++){
                prod*=i;
            }
            sum+=prod;
            num/=10; //same concept as in Task 1
        }
        if(sum==i){
            printf("%d, ",i);
        }
    }
    printf("\n"); //Changing the line for input of next part



    /*Part II*/

    //Task 1
    printf("Write number x: ");
    scanf("%d",&x); //variable x is reused from Part I
    sum=0; //variable sum is reused from Part I
    for(int i=1;i<=x/2;i++){ //checking till x/2 is fine because largest divisor of x which is not x itself will be <=x/2
        if(x%i==0){
            sum+=i;
        }
    }
    if(sum==x){
        printf("%d is a KGP number\n",x);
    }else{
        printf("%d is not a KGP number\n",x);
    }

    //Task 2
    printf("Write number n: ");
    scanf("%d",&n);  //variable n is reused from Part I
    printf("The KGP numbers between 1 and %d: ",n);
    for(int i=6;i<=n;i++){  //i starts from 6 since there exits no KGP number smaller than 6
        sum=0; //variable sum is reused from Part I
        int num=i; //same concept as in Part I Task 2
        for(int j=1;j<=num/2;j++){
            if(i%j==0){
                sum+=j;
            }
        }
        if(sum==i){
            printf("%d, ",i);
        }
    }
    printf("\n"); //Changing the line for input of next part



    /*Part III*/
    printf("Write a positive integer n: ");
    scanf("%d",&n);  //variable n is reused from Part I
    printf("Star K of height %d is\n",2*n-1);
    for(int i=0;i<2*n-1;i++){ //variable i takes care of all 2*n-1 rows of the pattern
        if(i<n){              // takes care of the upper halve of the pattern including the middle single star
            for(int j=n-i;j>=1;j--){ //variable j takes care of number of * in each row
                printf("* ");
            }
        }else{                 // takes care of the lower halve of the pattern after the middle single star
            for(int j=1;j<=i-n+2;j++){ // j goes from 1 till i-n+2 because as i changes from n to 2*n-2 this upper limit changes from 2 to n
                printf("* ");
            }
        }
        printf("\n"); // changing line after printing each row to move to the next row
    }


    return 0;
}

