int main(){
    int a;
    while(1){
        printf("Enter the number: ");
        scanf("%d", &a);
        if (a==0) break;
        printf("%d\n", a);
        char roman_num[15], roman_letters[5]={'C', 'L', 'X', 'V', 'I'};
        int digit=a/100, pos=digit;
        for (int i=0; i<digit; i++){
            roman_num[i]=roman_letters[0];
        }
        digit=(a%100)/10;
        for (int i=1; i<4; i+=2){
            if (digit==4 || digit==9){
                roman_num[pos]=roman_letters[i+1];
                switch (digit){
                    case 4: roman_num[pos+1]=roman_letters[i]; break;
                    case 9: roman_num[pos+1]=roman_letters[i-1]; break;
                }
                //OR roman_num[pos+1]=roman_letters[i-(digit/5)];
                pos+=2;
            }
            else{
                if (digit>=5){
                    roman_num[pos]=roman_letters[i];
                    pos+=1;
                    digit-=5;
                }
                for (int j=pos; j<digit+pos; j++){
                    roman_num[j]=roman_letters[i+1];
                }
                pos+=digit;
            }
            digit=a%10;
        }
        roman_num[pos]='\0';
        printf("%d in Roman Numerals: %s\n\n", a, roman_num);
    }
}

