#include<stdio.h>

int main(){

    /*Step 1*/
    //Taking input of array A and B sizes
    int n,m;
    printf("Enter positive integer n: ");
    scanf("%d",&n);
    printf("Enter positive integer m: ");
    scanf("%d",&m);

    /*Step 2*/
    int a[100],b[100]; //maximum size of array A and B are taken 100 each and these two integer arrays are declared
    //Taking input in A
    printf("Enter %d positive integers for array A: ",n);
    for(int i=0;i<n;i++){
        int k;
        scanf("%d",&k);
        a[i]=k;
    }
    //Taking input in B
    printf("Enter %d positive integers for array B: ",m);
    for(int i=0;i<m;i++){
        int k;
        scanf("%d",&k);
        b[i]=k;
    }

    /*Step 3*/
    int c[200],d[200]; //maximum size of array C and D are taken 200 each
    //C stores the distinct elements in A and B and D stores frequency of each element in C at corresponding indices

    /*Step 4*/
    //In this step we first swap even and odd elements of the array
    //This is done so that even elements accumulate at the beginning of the array and odd elements at the end
    int count,size=0;
    //count ensures if an element is array A or B is already present in C so that repeated elements dont go in C
    //size maintaines the size of the array C which increases with each new element insertion in it
    for(int i=0;i<n;i++){ //checking for distinct elements in array A
        count=0;
        for(int j=0;j<size;j++){
            if(c[j]==a[i]){
                count=1;
                d[j]++; //frequency of the already present elements are incresed by one in array D if they are encountered again
            }
        }
        if(count==0){
            c[size]=a[i]; //new distinct element gets appended at end of C
            d[size]=1;
            size++;
        }
    }
    for(int i=0;i<m;i++){ //checking for distinct elements in array B
        count=0;
        for(int j=0;j<size;j++){
            if(c[j]==b[i]){
                count=1;
                d[j]++;
            }
        }
        if(count==0){
            c[size]=b[i];
            d[size]=1;
            size++;
        }
    }

    //This segment deals with the process of swapping even and odd elements
    int l=0,u=size-1; //l searches for odd elements from front and u for even elements from end
    //once found elements at l and u are swapped
    while(1){
        while(c[l]%2==0 && l<size && l<u){
            l++;
        }
        while(c[u]%2!=0 && u>0 && l<u){
            u--;
        }
        if(l>=u){
            break; //once l>=u is satisfied we need to stop swapping so that already placed elements dont get again swapped
        }else{
            //swapping elements at l and u along with their corresponding frequencies
            int temp=c[l];
            c[l]=c[u];
            c[u]=temp;
            temp=d[l];
            d[l]=d[u];
            d[u]=temp;
        }
    }
    
    //Now in this part we sort the even and odd elements using bubble sort technique 
    //Sorting even elements in ascending order
    /*Actually here l obtained is the number of even elements in the array C
    and u indicates the index from where odd elements begin to appear*/
    for(int i=0;i<l-1;i++){
        for(int j=0;j<l-i-1;j++){
            if(c[j]>c[j+1]){
                int temp=c[j];
                c[j]=c[j+1];
                c[j+1]=temp;
                temp=d[j];
                d[j]=d[j+1];
                d[j+1]=temp;
            }
        }
    }

    //sorting odd elements in descending order
    for(int i=0;i<size-l-1;i++){
        for(int j=u;j<size-i-1;j++){
            if(c[j]<c[j+1]){
                int temp=c[j];
                c[j]=c[j+1];
                c[j+1]=temp;
                temp=d[j];
                d[j]=d[j+1];
                d[j+1]=temp;
            }
        }
    }

    /*Step 5*/
    //Here we print the numbers in array C and their corresponding frequencies 
    printf("The required sorting of the distinct numbers of A and B are: \n");
    for(int i=0;i<size;i++){
        printf("%d (%d)\n",c[i],d[i]);
    }

    return 0;
}

#include<stdio.h>
#include<string.h>

//Function to calculate the hamming distance between two strings
int hamming(char *a, char *b){
    //l1 and l2 are lengths of the two strings found using the string.h function strlen()
    int l1 = strlen(a);
    int l2 = strlen(b);
    //h_len is the required hamming distance and is calculated according to the rules given in the question and then returned
    int h_len=0;
    if(l1>l2){
        for(int i=0; i<l2; i++){
            if(a[i]!=b[i]) h_len++;
        }
        h_len = h_len + (l1-l2);
    }else{
        for(int i=0; i<l1; i++){
            if(a[i]!=b[i]) h_len++;
        }
        h_len = h_len + (l2-l1);
    }
    return h_len;
}

int main(){
    //Structure user is declared as is asked in the question having two strings members, username and password
    struct user{
        char username[101];
        char password[101];
    };
    //n denotes number of entries which is taken as input
    int n;
    printf("Enter positive integer: ");
    scanf("%d",&n);
    struct user users[n]; //array of structures of users type that holds username and password of each user
    for(int i = 1; i <= n; i++){
        printf("Enter the username of user %d: ",i);
        //i-1 is used because the array users[] has 0 based indexing
        scanf("%s",users[i-1].username);
        //now we check if the hamming of this entered username with any previous username is 0 or not
        //if 0 then that means we need to ask the user to again give input a new username and so we set j to -1 so that
        //the loop starts over again checking the usernames one by one from starting with the new input
        for(int j=0;j<i-1;j++){
            if(hamming(users[j].username,users[i-1].username)==0){
                printf("This username is already taken. Enter the username of user %d: ",i);
                scanf("%s",users[i-1].username);
                j=-1;
            }
        }
        //when the username is a unique one, then the control comes out of the loop and asks the user to enter the password
        printf("Enter the password of user %d: ",i);
        scanf("%s",users[i-1].password);
    }
    //now we declare 2 integer variables. checker is to make sure if turns was made 0 when operation was successful or if it just happened
    //that user gave three wrong try. turns takes care when to terminate the loop as soon as the user gives three wrong try or when
    //operation is successful
    int checker = 1;
    int turns = 3;
    while(turns){
        //these two strings are used to take the username and password entered by the user to compare them with the data
        char user_name[101];
        char pass_word[101];
        int check=0,pos=0; //pos stores the position where the username matches with one in the data
        printf("Enter your username to login: ");
        scanf("%s",user_name);
        printf("Enter your password to login: ");
        scanf("%s",pass_word);
        //this loop checks if the username is present in the data at all and accordingly assigns value to check
        for(int i=0; i<n; i++){
            if(hamming(user_name,users[i].username)==0){
                check=1;
                pos=i;
                break; //no need to check further
            }
        }
        if(check==1){
            //if password also matches then login is shown successful
            if(hamming(users[pos].password,pass_word)==0){
                printf("Login was successful!!");
                //turns made to 0 to exit from the while loop and checker ensures that operation was successful
                turns =0;
                checker =0;
            }else{
                //else we move into a while loop from which we can exit only if the password has been reset
                while(1){
                    //new_pass string stores the new password entered by the user
                    char new_pass[101];
                    int i;
                    //i keeps track of the current position in new_pass where a vowel is found and if it is equal to length of new_pass
                    //then no vowel found is indicated
                    printf("Your password does not match, please enter a new password: ");
                    scanf("%s",new_pass);
                    //len stores the length of new_pass
                    int len = strlen(new_pass);
                    //checking if vowel is present
                    for(i=0 ; i<len ; i++){
                        char ch = new_pass[i];
                        if(ch=='a'|| ch=='e'|| ch=='i'|| ch=='o'|| ch=='u'|| ch=='A'|| ch=='E'|| ch=='I'|| ch=='O'|| ch=='U'){
                            break;// no need to check further once a vowel is found
                        }
                    }
                    //ham_count stores the hamming distance between new password entered and the original password in data
                    //reqd stores the value of length of concatenated string formed by joining old and new password divided by 3
                    int ham_count = hamming(users[pos].password,new_pass);
                    int reqd = (strlen(users[pos].password)+len)/3;
                    //required condition is checked as given in the question
                    if(i<len && ham_count<=reqd){
                        //if condition satisfies then strcpy() copies the new password into the old password and a password set
                        //message is displayed
                        strcpy(users[pos].password,new_pass);
                        printf("Thank you, your password has been reset.");
                        //turns made to 0 to exit from the outer while loop and checker ensures that operation was successful
                        turns=0;
                        checker=0;
                        //break is used to exit from the inner infinite loop
                        break;
                    }
                }
            }
        }else{
            //displayed if username entered is not registered
            printf("This username is not registered! Please consider registering this username\n");
            turns--; //turns is decreased by one
        }
    }
    if(checker){
        //this tells that the outer while loop ended with wrong attempts
        printf("You have used up all your attempts!!");
    }
    return 0;
}

