                xw++;
                printf("A s badge: 1 B s badge: 0\n");
                xp+=xs;
                yp+=ys;
                printf("--A wins round %d--\n",i+1);

            }else{
                yw++;
                printf("A s badge: 0 B s badge: 1\n");
                xp+=xs;
                yp+=ys;
                printf("--B wins round %d--\n",i+1);

            }
        }
    }

    printf("Final result => A's total score: %d, B's total score: %d\n",xp*xw,yp*yw);
