/*a program to input n integers from the keyboard and store them within an array. Declare
another array of size n and copy the contents of the first array into the second array as per the
following rules:*/

#include<stdio.h>
#include<math.h>

int main()
{
    //variable initialization
    int n;double sum=0.0;
    double sq=0.0;

    //taking the array size
    printf("Input array size=");
    scanf("%d",&n);

    //array declartion
    int a[n],b[n];
    printf("enter array elements\n");

    //taking array input
    for(int i=0;i<n;i++)
    {
        printf("Enter the element A[%d]=",i);
        scanf("%d",&a[i]);

    }

    //performing the calculations
    for(int i=0;i<n;i++)
    {
        //swapping if even
        if((i+1)%2==0)
        {
            b[n-i]=a[i];
            

        }

        //calculated for non-even digits
        else
        {
            for(int j=i+1;j<n;j++)
            {
                sum=sum+pow(a[j],2);

            }
            
            //taking square root and rounding off
            sq=round(sqrt(sum));
            
            b[i]=a[i]*sq;
            sum=0.0;

            
        }
        
    }

    // printing the new array
    printf("\n The new array :");
    for (int i=0;i<n;i++)
    {
        printf("\nElement B[%d]=%d",i,b[i]);
    }
    
    // completion of the program
    return 0;
}

/*program to find mean,standard deviation and grades of student */

#include<stdio.h>
#include<math.h>

int main()
{
    //variable and array declaration
    int a[10],sum=0;
    double mean=0.0,sd=0.0;

    //taking input of marks
    printf("Enter the marks of students\n");
    for (int i=0;i<10;i++)
    {
        printf("Student %d=",i+1);
        scanf("%d",&a[i]);

        //if marks not in range
        while(a[i]>100||a[i]<0)
        {
            printf("Error,please enter marks within range\nStudent %d=",i+1);
            scanf("%d",&a[i]); 
        }
    }

    // finding mean 
    for(int i=0;i<10;i++)
    {
        sum=sum+a[i];
    }
    mean=sum/10.0;

    // finding standard deviation
    for (int i=0;i<10;i++)
    {
        sd=sd+pow((a[i]-mean),2);
    }
    sd=sqrt(sd/10);

    //cgecking for grades and printing them
    for(int i=0;i<10;i++)
    {
        if (a[i]>=(mean+1.5*sd))
        printf("\nGrade of Student %d=10",i+1);

        else if (a[i]<(mean+1.5*sd)&&a[i]>=(mean+1.0*sd))
        printf("\nGrade of Student %d=9",i+1);

        else if (a[i]<(mean+1.0*sd)&&a[i]>=(mean+0.5*sd))
        printf("\nGrade of Student %d=8",i+1);

        else if (a[i]<(mean+0.5*sd)&&a[i]>=(mean+0*sd))
        printf("\nGrade of Student %d=7",i+1);

        else if (a[i]<(mean-0.0*sd)&&a[i]>=(mean-0.5*sd))
        printf("\nGrade of Student %d=6",i+1);

        else if (a[i]<(mean-0.5*sd)&&a[i]>=(mean-1.0*sd))
        printf("\nGrade of Student %d=5",i+1);

        else if (a[i]<(mean-1.0*sd)&&a[i]>=(mean-1.5*sd))
        printf("\nGrade of Student %d=4",i+1);

        else 
        printf("\nGrade of Student %d=FAIL",i+1);
    }

    //program completed
    return 0;
}

/*program to play the game of buckets*/
#include<stdio.h>
#include<math.h>

int main()
{
    //taking buckets input
   int n;
   printf("Enter Number of Buckets:");
   scanf("%d",&n);
   int a[n];

   //array input
   printf("Enter Positive Number of Balls for Each %d Buckets:",n);
   for (int i=0;i<n;i++)
   {
       scanf("%d",&a[i]);
   }

   //calucaltion of iterations
   int nr=0,y=1;
    while (1)
    {
        printf("——–Iteration-%d———\n",y);
        int l=1,b;
        for (int i=0; i < n; i++)
        {
            if (a[i]>0)
            {
                b=a[i];
                break;
            }
        }
        for (int i=1;i<n;i++)
        {
            if (a[i]<=0)
            {
                continue;
            }
            else if (a[i]<b)
            {
                l=1;
                b=a[i];
            }
            else if (a[i]==b)
            {
                l++;
            }
        }
        nr=nr+l;
        for (int j=0;j<n;j++)
        {
            a[j]=a[j]-b;
        }
        printf("Number of Buckets with least number of Balls = %d\n", l);
        printf("Number of balls in the Remaining Buckets = ");
        y++;
        for (int k = 0; k < n; k++)
        {
            if (a[k]<=0)
            {
                continue;
            }
            printf(" %d", a[k]);
        }
        if (nr>=n)
        {
            printf("NIL[END]");
            break;
        }
        printf("\n");
        printf("\n");
    }

    //program completed
    return 0;
}

