	{
		arr[8*count+j]=n%2;
		n=n/2;
	}
}

void print(int n)
{
	if (n<26) printf("%c",(n+65));
	else if (n<52) printf("%c",(n+71));
	else if (n<62) printf("%c",(n-4));

}

void part2(int n, int no[],int count)
{
	int j;
	for (j=5;j>=0;j--)
	{
		no[6*count+j]=n%2;
		n=n/2;
	}
}

