int main()
{
    int a,b,arr_a[100],arr_b[100];
    int arr_union[200], arr_inter[100], arr_diff[100];
    //arr_union is the array that will store the union of sets A and B
    //arr_inter is the array that will store the intersection of sets A and B
    //arr_diff is the array that will store those values of A that are not present in B

    //reading the value of integers a and b
    printf("Enter the value of a (length of first array): ");
    scanf("%d",&a);
    printf("Enter the value of b (length of second array): ");
    scanf("%d",&b);
    printf("\n");

    //reading a integer values and storing them in an array
    printf("Enter %d integer values: ",a);
    for(int i=0;i<a;i++)
    {
        scanf(" %d",&arr_a[i]);
    }

    //reading b integer values and storing them in an array
    printf("Enter %d integer values: ",b);
    for(int i=0;i<b;i++)
    {
        scanf(" %d",&arr_b[i]);
    }


    for(int i=0;i<a;i++)
    {
        arr_union[i]=arr_a[i];      //all elements of A will be added in union set
    }

    int j=a;
    int index_intersection=0;
    int index_diff=0;
    //index_intersection is used for storing indices of array arr_inter
    //index_diff is used for storing indices of array arr_diff


    for(int i=0;i<b;i++)
    {
        int flag_union=0;
        for(int k=0;k<a;k++)
        {
            if(arr_union[k]==arr_b[i])
            {
                flag_union++;
            }
            if(arr_a[k]==arr_b[i])
            {
                arr_inter[index_intersection]=arr_a[k];
                index_intersection++;
            }
        }
        if(flag_union==0)       //elements of B will be added to union only if they are not in A
        {
            arr_union[j]=arr_b[i];
            j++;
        }
    }

    for(int i=0;i<a;i++)
    {
        int flag_diff=0;
        for(int k=0;k<b;k++)
        {
            if(arr_a[i]==arr_b[k])
            {
                flag_diff++;
            }
        }
        if(flag_diff==0)        //elements of set A will be added to arr_diff only if they are not present in set B
        {
            arr_diff[index_diff]=arr_a[i];
            index_diff++;
        }
    }

    //printing the union array
    printf("\nThe Union array (A U B) is: \n");
    for(int i=0;i<j;i++)
    {
        printf("%d ",arr_union[i]);
    }
    printf("\n");

    //printing the intersection array
    printf("\nThe Intersection array is: \n");
    for(int i=0;i<index_intersection;i++)
    {
        printf("%d ",arr_inter[i]);
    }
    printf("\n");

    //printing the difference array
    printf("\nThe Difference array (A-B) is: \n");
    for(int i=0;i<index_diff;i++)
    {
        printf("%d ",arr_diff[i]);
    }
    printf("\n");

    return 0;
}

#include <stdio.h>
