int countdistinct(int[], int);      //function to count number of distinct elements in input array
void copy_distinct(int*,int*,int);  //function to copy distinct elements of an array into another

int main()
{
    int n, array_input[100],count_distinct;
    //n is an integer variable that stores the number of integers the user wants to enter
    //array_input stores the input values
    //count_distinct stores the number of distinct values in input array

    //reading n
    printf("Enter n: ");
    scanf("%d",&n);

    //reading values of array
    printf("Enter %d values:\n",n);
    for(int i=0;i<n;i++)
    {
        scanf("%d",&array_input[i]);
    }

    //counting the number of distinct elements
    count_distinct=countdistinct(array_input,n);

    //dynamic allocation of memory
    int *point_new_array;
    point_new_array=(int*)malloc(count_distinct*sizeof(int));

    //if system is unable to allocate memory, then exit
    if  (point_new_array == NULL)
    {
		  printf ("\nMemory cannot be allocated");
		  exit(0);
	}

	//copying the distinct elements from input array into new array
    copy_distinct(array_input,point_new_array,n);

    //printing the new array
