/*
*Function: countdistinct
*returns the number of distinct elements in input array
*
* a[]: the input array
* n: the number of elements in input array
*
*returns: number of distinct elements in input array
*/
int countdistinct(int a[], int n)
{
    if(n==0) return 0;
    int i,j,count=1;

    for (i=1;i<n;i++)
    {
      for (j=0; j<i;j++)
      {
         if (a[i]==a[j])
         {
            break;             //if same element is found then break
         }
      }
      if (i==j)
      {
         count++;     //increment the number of distinct elements
      }
    }
   return count;      //returns the number of distinct elements
}

/*
*Function: copy_distinct
*copies the distinct elements of first array into second array
*
* p: pointer to first elements of first array
* q: pointer to first element of second array
*
*return type: void
*/
void copy_distinct(int *p,int *q,int n)
{
    int k=0;

    for(int i=0;i<n;i++)
    {
        int flag=0;
        for(int j=0;j<k;j++)
        {
            //checking if that element if stored in q before
            if(p[i]==q[j])
            {
                flag=1;
                break;
            }
        }
        if(flag==0)
        {
            //copying value in new array location if it is not previously copied
            q[k]=p[i];
            k++;
        }
    }

    return;
}

