int innercheck(int x, int y, int z, int w, int p, int q, int r, int s) // method to check if one rectangle lies inside another
{
    int xbleft1 = x; // contains bottom left corner  of first rectangle
    int ybleft1 = y;
    int xtright1 = x + z; // contains top right corner of second rectangle
    int ytright1 = y + w;
    int xbleft2 = p;
    int ybleft2 = q; // returns 1 if contained else 0 is returned
    int xtright2 = p + r;
    int ytright2 = q + s;
    if (xbleft2 > xbleft1 && ybleft2 > ybleft1 && xtright1 > xtright2 && ytright1 > ytright2)
    {
        printf("%d %d %d %d contains %d %d %d %d\n", x, y, z, w, p, q, r, s);
        return 1;
    }
    else
        return 0;

} // the property of a rectangle is that the point of intersection of diagonals is the midppoint of the diagonals.So that logic is used in the code.
double distorigin(int x, int y, int z, int w) // method to calculate distance from origin
{
    double midx = (double)(2 * x + z) / 2;
    double midy = (double)(2 * y + w) / 2;
    double dist = (sqrt(midx * midx + midy * midy));
    return dist;
}
double distpoint(int x, int y, int z, int w, int k, int l) // method to calculate distance of the centres from the entered point
{
    double mid1 = ((double)(2 * x + z)) / 2;
    double mid2 = ((double)(2 * y + w)) / 2;
    double distance = (sqrt((mid1 - k)*(mid1 - k) + (mid2 - l)*(mid2 - l)));
    return distance;
}
