#include <stdio.h>
#include <math.h>

float convertRealNumber(char length[]){
    int digit, j=0, decimal=0;
    float num=0;

    //converting real number in string to float 
    while (length[j]!=' '){
        digit=length[j]-48;
        if (length[j]=='.'){
            decimal=j;
            j+=1;
            continue;
        }
        if (decimal==0){
            num=(num*10)+digit;
        }
        else {
            num+=(digit*pow(0.1, j-decimal));
        }
        j+=1;
    }

    //converting ft or cm to meters
    if (length[j+1]=='f'){
        num*=0.3048;
    }
    else {
        num*=0.01;
    }
    return num;
}

