void exchange(int a[], int n)
{
    printf("[");
    for (int i = 0; i < n; i++)
    {
        printf("%d ,", a[i]);
    }
    printf("]\n");

    if (n % 2 == 0){ //case for even no. of elements
        for (int i = 0; i < n / 2; i++)
        {
            int temp;
            temp = a[i];
            a[i] = a[n / 2 + i];
            a[n / 2 + i] = temp;
        }
    }

    else { //case for odd no. of elements
        for (int i = 0; i < n / 2; i++)
        {
            int temp;
            temp = a[i];
            a[i] = a[n / 2 + i + 1];
