void rr(int a[], int n)
{
    printf("[");
    for (int i = 0; i < n; i++)
    {
        printf("%d ,", a[i]);
    }
    printf("]\n");

    int temp = a[n - 1]; //temp stores the original value of a[n-1]

    for (int i = n - 1; i > 0; i--)
    {
        a[i] = a[i - 1];
    }
    a[0] = temp; //assignment of a[0] to a[n-1]

    printf("[");

    for (int i = 0; i < n; i++)
    {
        printf("%d ,", a[i]);
    }
    printf("]\n");
}

void rl(int a[], int n)
{
    printf("[");
    for (int i = 0; i < n; i++)
    {
        printf("%d ,", a[i]);
    }
    printf("]\n");
    int temp = a[0]; //temp stores the original value of a[0]
    for (int i = 0; i < n - 1; i++)
    {
        a[i] = a[i + 1];
    }
    a[n - 1] = temp; //assignment of a[n-1] to a[0]
    printf("[");
    for (int i = 0; i < n; i++)
    {
        printf("%d ,", a[i]);
    }
    printf("]\n");
}

void sort(int a[], int n)
{
    printf("[");
    for (int i = 0; i < n; i++)
    {
        printf("%d ,", a[i]);
    }
    printf("]\n");
    for (int i = 0; i < n - 1; i++)
    {
        for (int j = i + 1; j < n; j++)
        {
            if (a[i] > a[j])
            {
                int temp;
                temp = a[i];
                a[i] = a[j];
                a[j] = temp;
            }
        }
    }
    printf("[");
    for (int i = 0; i < n; i++)
    {
        printf("%d ,", a[i]);
    }
    printf("]\n");
}

