void findmax( int m, int n, int a[m][n]){
    int sum=0, row=0, column=0,max=0;
    
     for(int i=0; i<m-2;i++){
        for(int j=0; j<n-2; j++){
            //finds the sum of all 3*3 matrix possible
            sum= a[i][j]+ a[i][j+1]+ a[i][j+2]+a[i+1][j]+ a[i+1][j+1]+ a[i+1][j+2]+a[i+2][j]+ a[i+2][j+1]+ a[i+2][j+2];
            //assignment of rows and columns
            if(sum>max){
                max=sum;
                row=i; column=j;
            }
        }
    }
    for(int i= row; i<row+3; i++){
        for(int j=column; j<column+3; j++){
            printf("%d  ", a[i][j]);
        }
        printf("\n");
    }
}

