int isFascinating(int n){ //n is a positive integer which this function takes as input in it's parameter
    int num1=n*2; //Number obtained by multiplying n by 2
    int num2=n*3; //Number obtained by multiplying n by 3
    int arr[10]; /*Array to store number of times each digit from 0 to 9 appears in all the three numbers n , num1 and num2
                 such that arr[i] stores number of times i occurs in all the three numbers*/

    for(int i=0; i<10; i++){
        arr[i]=0; //Initializing all the elements of the array with 0
    }

    //Counting number of times each digit appears in n and adding the data in array arr
    while(n>0){
        int k=n%10; //k stores each digit of n
        arr[k]++;
        n/=10;
    }

    //Counting number of times each digit appears in num1 and adding the data in array arr
    while(num1>0){
        int k=num1%10; //k stores each digit of num1
        arr[k]++;
        num1/=10;
    }

    //Counting number of times each digit appears in num2 and adding the data in array arr
    while(num2>0){
        int k=num2%10; //k stores each digit of num2
        arr[k]++;
        num2/=10;
    }

    //Checking the data accumulated in array arr
    for(int i=1;i<=9;i++){
        if(arr[i]!=1){
            return 0; /*If any one of the digits from 1 to 9 is not present exactly once then immediately 0 is returned
                      because it violates the definition of Fascinating number. Number of time 0 occurs is not checked
                      because by definition 0 can exist multiple times or not be present in the list of digits*/
        }
    }

    return 1; //If all digits from 1 to 9 are present exactly once then the loop executes till last and 1 is returned
}


