int isArmstrong(int n){ //n is a positive integer which this function takes as input in it's parameter
    int arm_num=0; //arm_num stores the value of sum of, digits raised to the power number of digits
    int n_dig=digits(n); //n_dig stores the number of digits in n which it gets by calling the digits() function with n as its parameter
    int temp=n; //temp stores the value of n temporarily so that n is preserved for its comparison with arm_num later

    while(temp>0){
        int k=temp%10; //k stores each digit of temp
        arm_num+=(int)(pow(k,n_dig)); //value of digit to the power of number of digits is type casted from double to int and added to arm_num
        temp/=10;
    }

    if(n==arm_num){ //Checking the condition of being an Armstrong number
        return 1;
    }else{
        return 0;
    }
}



