#include<stdio.h>

int divisor(int k, int n, int sum){ //recursively calls itself with all numbers <=squareroot(n) where n is taken as parameter

    //k stores the value of all potential candidates which can be proper divisor of n till squareroot(n)
    //sum stores the sum of all the proper divisors of n

    if(n==1){ //special case of n=1 is handled with help this 'if' when 1 is returned to keep it wth prime numbers (1 is not a prime number)
        return 1;
    }

    if(k*k>n){ //base case of k>squareroot(n) where no more proper divisors of n which are more than this k exist so no need to check furthur
        return 0; //0 is returned which when added to sum, makes no change in sum
    }

    if(n%k==0){ //case when k is a proper divisor of n

        printf("%d, ",k); //k is printed
        sum=divisor(k+1,n,sum); //recursive call is made to check if k+1 is also a proper divisor of n

        /*When the recursive call returns the following 'if' checks if k!=1 so that n
        itself is not added to sum or printed and also checks if k is not equal to squareroot(n) so
        that k is not added twice or printed twice*/
        if(k!=1 && n/k!=k){
            sum+=n/k; //if n/k and k are distinct or k is not 1 then n/k is added to sum as it is also a proper divisor of n
            printf("%d, ",n/k); //this prints the proper divisor n/k of n corresponding to the proper divisor k
        }

        sum+=k; //k is added to sum since it is a proper divisor of n
        return sum; //new sum is returned to calling function

    }else{ //case when k is not a proper divisor of n

        sum=divisor(k+1,n,sum); //here next k+1 is called to see if it is a proper divisor of n
        /*once the function call returns to this else part nothing is added to sum since k is not a proper divisor of n
        sum is passed down to calling function as it is*/
        return sum;

    }

}

