int findSinX(float x,int n)
{
    float sinx;
    for (int i = 1; i <= n; i++)
    {

        float fac = 1.0;
        for (int j = 1; j <= (2 * i - 1); j++)
        {
            fac = fac * j;
        }
        if (i % 2 == 0)
        {
            sinx -= x / fac;
        }
        else
        {
            sinx += x / fac;
        }
        x *= x * x;
    }
    printf("sinx = %f", sinx);
    return 0;
}

