int pal(char S[],int i,int j)
{
    int k,test;
    test=1;
    for(k=0;k<((i+j)/2);k++){
        if(S[i+k]!=S[j-k]){
            test=0;
            {break;}
        }
    }
    return test;
}

int rpal(char S[], int i, int j)
{
    int test;
    test=1;
    if(i>=j)return test;
    if(S[i]!=S[j]){
        test=0;
        return test;
    }
    else if (S[i]==S[j]){
       test=rpal(S,i+1,j-1);
       return test;
    }
}

