int largek(int k, int x[], int size)
{
    int max_index=0;
    for (int i=0; i<size; i++)
    if (x[max_index]<x[i]) max_index = i;

    if (k==1) return x[max_index];
    else
    {
        x[max_index] = -2,147,483,648;
        return largek(k-1,x,size);
    }
}

