/*
*Function: n_choose_k
*returns the value of C(n,k) by using recursion
*
*total_elements: total elements(n) from which k elements have to be chosen
*elements_to_be_chosen: number of elements to be chosen from total_elements
*
*returns: the number of ways elements_to_be_chosen(k) items can be chosen from total_elements (n items)
*/
long int n_choose_k(int total_elements,int elements_to_be_chosen)
{
    if(elements_to_be_chosen==0) return 1;      //C(n,0)=1 always
    if(elements_to_be_chosen==1) return total_elements;     //C(n,1)=n always
    return n_choose_k(total_elements-1,elements_to_be_chosen-1)*total_elements/elements_to_be_chosen;       //C(n,k)=(n/k)*C(n-1,k-1)
}

