int bin_search(int x, int a[100], int s, int l)
{
  while (s <= l)
  {
    int m = s + (l-s)/2;
    if (a[m] == x)
        return m;
    if (a[m] < x)
        s = m + 1;
    else
         l = m - 1;
  }
  return -1;
}



int linear_search(int n,int x,int a[100]){

    for(int i=0;i<n;i++){
        if(a[i]==x){
            return i;
        }
    }

    return -1;

}




