void removeDuplicate(char *x[]){
    int z=0;
    for(int i=0; i<n-z; i++){
        for(int j=i+1; j<n-z; j++){
            if(strcmp(x[i],x[j])==0){// condition check
                free(x[j]);// deallocating the memory space
                z++; // counter is initialised to know the number of strings deallocated
