#include<stdio.h>

int main(){

    //Task 1
    struct num{
        int Bin[9]; //array for storing binary representation of number associated with A[i][j]
        int Oct[3]; //array for storing octal representation of number associated with A[i][j]
        int n_bin; //integer to store the length of the bin array of number associated with A[i][j]
        int n_oct; //integer to store the length of the oct array of number associated with A[i][j]
    };

    //Task 2
    struct num A[20][20]; //2D array of type num declared

    //Task 3
    //Taking input n and m from user
    int n,m;
    printf("Enter positive integer n less than equal to 20: ");
    scanf("%d",&n);
    printf("Enter positive integer m less than equal to 20: ");
    scanf("%d",&m);

    //Task 4
    for(int i=0;i<n;i++){
        for(int j=0;j<m;j++){
            int a;
            //a is taken as input whose binary and octal representation are to be stored in num in arrays Bin[] and Oct[] resepectively
            printf("Enter an integer between -500 and 500 for A[%d][%d]: ",i,j);
            scanf("%d",&a);
            if(a>=0){ //case when a is non-negetive
                //Special case fo a=0
                if(a==0){
                    //0 is stored in both arrays as binary and octal equivalent of 0 are 0 only
                    //1 is stored in lengths of both arrays
                    A[i][j].Bin[0]=0;
                    A[i][j].Oct[0]=0;
                    A[i][j].n_bin=1;
                    A[i][j].n_oct=1;
                }

                //a is stored in temp so that a is preserved for furthur use and count takes care of indexes of arrays in structure num
                int temp=a,count=0;

                //Storing the required binary representation of a in Bin[]
                while(temp>0){
                    A[i][j].Bin[count]=temp%2;
                    temp/=2;
                    count++;
                }

                if(a!=0){ //since case of a=0 is already handled separately
                    A[i][j].n_bin=count;
                }

                //temp and count are reused for use in Oct[]
                temp=a;
                count=0;

                //Storing the required octal representation of a in Oct[]
                while(temp>0){
                    A[i][j].Oct[count]=temp%8;
                    temp/=8;
                    count++;
                }

                if(a!=0){ //since case of a=0 is already handled separately
                    A[i][j].n_oct=count;
                }

            }else{ //case when a is negetive
                /*absolute value of a is stored in temp so that a is preserved for furthur use and 
                count takes care of indexes of arrays in structure num*/
                int temp=-a,count=0;

                //Storing the required binary representation of a in Bin[]
                while(temp>0){
                    A[i][j].Bin[count]=1-(temp%2); //here 1-(temp%2) is done to find 1's complement representation of a as it is negetive
                    temp/=2;
                    count++;
                }

                A[i][j].n_bin=count;
                //temp and count are reused for use in Oct[]
                temp=-a;
                count=0;

                //Storing the 7's complement octal representation of a in Oct[]
                while(temp>0){
                    A[i][j].Oct[count]=7-(temp%8); //here 7-(temp%2) is done to find 7's complement representation of a as it is negetive
                    temp/=8;
                    count++;
                }
                A[i][j].n_oct=count;

                //Now we change the octal representation form 7's complement to as required which is the 8's complement
                
                count=0; //resetting count to 0
                /*Initialising carry with 1. This carry will help to keep track of any carry during addition of 1
                to the 7's complement representation and at the same time since it initially 1 it also helps to
                start the process of adding*/
                int carry=1;
                while(count<A[i][j].n_oct){
                    if(A[i][j].Oct[count]+carry==8){ //when this condition is satisfied then a carry of 1 is introduced
                        A[i][j].Oct[count]=0; //digit which was 7 is changed to 0 with a carry of 1
                        carry=1; //carry is changed to 1 to take the carry furthur
                    }else{
                        A[i][j].Oct[count]+=carry; //carry is added to the digit if final digit is <=7
                        carry=0; //no need to take the carry furthur so it is made 0
                    }
                    count++; //count now moves to the next digit of the 7's complement
                }
                //Finally we check if any more carry is left. If yes then we append that to the end of the 8's complement
                if(carry==1){
                    A[i][j].Oct[count]=1;
                    count++; //count is modified to new value
                }
                A[i][j].n_oct=count; //length of the array Oct[] is modified to new value of count which may be the old one too
            }
        }
    }

    //Task 5
    int B[20][20]; //array B declared

    //Task 6
    for(int i=0;i<n;i++){
        for(int j=0;j<m;j++){
            /*Checking if there is no ith element in array Bin[](then element is taken to be 0) or Bin[i]=0 : 
            in any one case first condition is satisfied
            Then checking if there is no jth element in array Oct[](then element is taken to be 0 which is less than 4) or Oct[j]<=4 : 
            in any one case second condition is satisfied
            And finally we check if both first condition and second condition are satisfied, then only B[i][j] is 1 else 0*/
            if((i>=A[i][j].n_bin || A[i][j].Bin[i]==0) && (j>=A[i][j].n_oct || A[i][j].Oct[j]<=4)){
                B[i][j]=1;
            }else{
                B[i][j]=0;
            }
        }
    }

    //Task 7
    //Printing B as a 2D array
    printf("\nThe matrix B is as follows:\n");
    for(int i=0;i<n;i++){
        for(int j=0;j<m;j++){
            printf("%d ",B[i][j]);
        }
        printf("\n");
    }
    return 0;
}

