                ch2 = p[j+1][m];
                //ch1 and ch2 if in uppercase are changed to lowercase so that comparison of their ascii can be done fairly
                if(ch1>=65 && ch1<=90) ch1=ch1+32;
                if(ch2>=65 && ch2<=90) ch2=ch2+32;
                if(ch1>ch2){
                    check=1; //if p[j] is lexicographically greater than p[j+1] then they need to be swapped and so check is made one
                    break; //no need to check further from point of 1st dissimilarity
                }else if(ch1<ch2){
                    check=0; //if p[j] is lexicographically smaller than p[j+1] then they need not to be swapped and so check is made zero
                    break; //no need to check further from point of 1st dissimilarity
                }else{
                    check=2; //if p[j] is lexicographically equal to p[j+1] till this position then check is made two
                }
            }
            if(check==2){ //if check is 2 then either one is prefix of another or they are equal
                if(ch1!='\n') check=1; //if p[j] is prefix of p[j+1] then we do not swap
                else check=0;
            }
            if(check){ //swap only if check=1
                char *temp; //temporary variable to store value of p[j] which is address of p[j][0]
                //address of p[j][0] and p[j+1][0] are interchanged so that the pointers now point to interchanged strings
                //p[j] now points to the intial p[j+1] string and reverse for p[j+1]
                temp=p[j];
                p[j]=p[j+1];
                p[j+1]=temp;
                //same is done for q[j] and q[j+1]
                temp=q[j];
                q[j]=q[j+1];
                q[j+1]=temp;
            }
        }
    }
    //Final list of names sorted according to first name is printed
    printf("\n\nSorted according to first name:\n");
    for(int i=0; i<n; i++){
        printf("%s %s\n", p[i],q[i]);
    }

    //Same logic is for this part where we sort according to last name and so we deal with q instead of p
    for(int i=0; i<n-1; i++){
        for(int j=0;j<n-i-1;j++){
            int m;
            int check;
            char ch1 = q[j][0];
            char ch2 = q[j+1][0];
            for(m=0;ch1!='\n' && ch2!='\n';m++){
                ch1 = q[j][m];
                ch2 = q[j+1][m];
                if(ch1>=65 && ch1<=90) ch1=ch1+32;
                if(ch2>=65 && ch2<=90) ch2=ch2+32;
                if(ch1>ch2){
                    check=1;
                    break;
                }else if(ch1<ch2){
                    check=0;
                    break;
                }else{
                    check=2;
                }
            }
            if(check==2){
                if(ch1!='\n') check=1;
                else check=0;
            }
            if(check){
                char *temp;
                temp=p[j];
                p[j]=p[j+1];
                p[j+1]=temp;
                temp=q[j];
                q[j]=q[j+1];
                q[j+1]=temp;
            }
        }
    }
    //Final list of names sorted according to last name is printed
    printf("\n\nSorted according to last name:\n");
    for(int i=0; i<n; i++){
        printf("%s %s\n", p[i],q[i]);
    }
}

