#include <stdio.h>

//defining a structure circle with members: x coordinate of centre, y coordinate of centre and radius of circle
struct circle
{
    float centre_x;     //x coordinate of centre
    float centre_y;     //y coordinate of centre
    float radius;       //radius of circle
};

int intersection(struct circle, struct circle); //function to check whether two circles intersect or not

int main()
{
    //declaring two circles
    struct circle circle_first, circle_second;

    //reading parameters for first circle
    printf("Enter x coordinate of centre of first circle: ");
    scanf("%f",&circle_first.centre_x);
    printf("Enter y coordinate of centre of first circle: ");
    scanf("%f",&circle_first.centre_y);
    printf("Enter radius of first circle: ");
    scanf("%f",&circle_first.radius);

    //reading parameters for second circle
    printf("\nEnter x coordinate of centre of second circle: ");
    scanf("%f",&circle_second.centre_x);
    printf("Enter y coordinate of centre of second circle: ");
    scanf("%f",&circle_second.centre_y);
    printf("Enter radius of second circle: ");
    scanf("%f",&circle_second.radius);

    if(intersection(circle_first,circle_second))
    {
        //if circles intersect
        printf("\nThe two circles intersect (or touch).\n");
    }
    else
    {
        //if circles do not intersect
        printf("\nThe two circles neither intersect nor touch each other.\n");
    }
    return 0;
}

/*
*Function: intersection
*returns whether the two circles intersect or not
*
*first_circle: circle structure that takes parameters of first circle
*second_circle: circle structure that takes parameters of second circle
*
*returns: 1 if the circles intersect, 0 if they don't
*/
int intersection(struct circle first_circle, struct circle second_circle)
{
    //circles intersect if they cut each other. This means, if OP<=r1+r2 and OP>=|r1-r2|. if one circle lies completely inside the other circle, then they donot cut and hence, are non-intersection.
    //OP=distance between centres of circles, r1 and r2 are radii of both circles respectively.
    if((first_circle.centre_x-second_circle.centre_x)*(first_circle.centre_x-second_circle.centre_x)+(first_circle.centre_y-second_circle.centre_y)*(first_circle.centre_y-second_circle.centre_y)<=(first_circle.radius+second_circle.radius)*(first_circle.radius+second_circle.radius) && (first_circle.centre_x-second_circle.centre_x)*(first_circle.centre_x-second_circle.centre_x)+(first_circle.centre_y-second_circle.centre_y)*(first_circle.centre_y-second_circle.centre_y)>=(first_circle.radius-second_circle.radius)*(first_circle.radius-second_circle.radius))
    {
        //if the two circles cut(intersect)
        return 1;
    }
    else
    {
        //if the two circles do not cut each other
        return 0;
    }
}

