    for(length=0; password[length]!='\0'; ++length);

    //if length is less than 8, then flag remains 0
    if(length>=8)
    {
        //if length is atleast 8, then further conditions are checked.
        for(int i=0;password[i]!='\0';i++)
        {
            if(password[i]>='A' && password[i]<='Z')
            {
                //set flag_uppercase to 1 if an upper-case alphabet is found
                flag_uppercase=1;
            }
            if(password[i]>='0' && password[i]<='9')
            {
                //set flag_digit to 1 if a digit is found
                flag_digit=1;
            }
            if(flag_uppercase==1 && flag_digit==1)
            {
                //set flag to 1 and break out of the loop as soon as all conditions are satisfied
                flag=1;
                break;
            }
        }
    }

    //printing the result
    if(flag==1)
    {
        //if all conditions are satisfied, i.e., flag is 1.
        printf("Password is strong.");
    }
    else
    {
        //if all conditions are not satisfied, i.e., flag is not 1.
        printf("Password is not strong.");
    }


    return 0;
}

