/*
*Function: find_length
*returns the length of the input string
*
* string[]: stores the input string as a null terminated character array
*
*returns: number of characters in input string
*/
int find_length(char string[])
{
    int i=0;
    //i is an integer variable used to store the number of characters in the string

    //traversing through the character array untill the terminating character is found
    while(string[i]!='\0')
    {
        i++;
    }
    return i;      //returning the number of characters in the input string
}

/*
*Function: newlength_no_vowels
*returns the length of the string without vowels
*
* string[]: stores the input string as a null terminated character array
*
*returns: number of characters except vowels in input string
*/
int newlength_no_vowels(char string[])
{
    int i=0,len=0;
    //i is a loop variable used to traverse through the string
    //len is in an integer variable used to store the length of the string
    while(string[i]!='\0')
    {
        if(string[i]!='A' && string[i]!='E' && string[i]!='I' && string[i]!='O' && string[i]!='U')
        {
            //len will be incremented only if the character is not a vowel
            len++;
        }
        i++;
    }
    return len;     //returns the length of the string without vowels
}

/*
*Function: copy_no_vowels
*copies the string to a new memory location except vowels
*
* string[]: stores the input string as a null terminated character array
* p: character pointer where new string will be stored
*
*returns type: void
*/
void copy_no_vowels(char string[],char *p)
{
    int i=0,j=0;
    //i is a loop variable used to traverse through the string
    //j is in an integer variable used for location of characters of new string

    while(string[i]!='\0')
    {
        if(string[i]!='A' && string[i]!='E' && string[i]!='I' && string[i]!='O' && string[i]!='U')
        {
            //character will be copied only if it is not a vowel
            p[j]=string[i];
            j++;
        }
        i++;
    }
    p[j]='\0';
    return;
}

