void findMinMax(int **a, int r, int c){
    int max, min;
    max=min=a[0][0];
    int rmin,rmax,cmin,cmax;
    for(int i=0; i<r; i++){
        for(int j=0; j<c; j++){
            if(a[i][j]>max){
                max=a[i][j];
                rmax=i;
                cmax=j;
            }
        }
    }
    for(int i=0; i<r; i++){
        for(int j=0; j<c; j++){
            if(a[i][j]<min){
                min=a[i][j];
                rmin=i;
                cmin=j;
            }
        }
    }
    printf("The maximum element present in matrix is %d, present at row no. %d and column no. %d.\n", max, rmax+1, cmax+1);
    printf("The minimum element present in matrix is %d, present at row no. %d and column no. %d.\n", min, rmin+1, cmin+1);
}

