int isSawtooth(int *arr,int n){

    int x,y,flag=1;

    for(int i=0;i<n-3;i++){

        x = arr[i+1] - arr[i];
        y = arr[i+2] - arr[i+1];

        if(x*y>=0){
            flag=0;   // if the product of differences is positive that means both have the same sign, so not sawtooth
            break;
        }
    }
    return flag;
}

void position(int *arr,int *pos, int n){
    int x,y;
    for(int i=0;i<n-2;i++){

        x=arr[i+1]-arr[i];
        y=arr[i+2]-arr[i+1];

        if(x*y>=0){     // Same logic as in is Sawtooth function
            *pos = i+3; // i+3 to convert from 0->n-1 to 1->n
            return;
        }
    }
    return;
}

/*
 Takes user input as 2D character array (ie, 1D array of strings)
    Following operations are performed on the array:
    1) void characterCount(char* str, int* vowel, int* consonant) - finds number of vowels and consonants and assigns it through
	respective pointer
    2) int wordCount(char* str) - returns no. of words in string
    3) void excessWord(char* str1, char* str2) - checks wordcount of both strings, and prints words which are excess in larger string
    4) void str_pair_compute(char **arr, int number_of_strings) - takes entire 2d array + number of strings in it as input,
	displays positions that contain same word
*/
#include<stdio.h>
#include<string.h>
//Declaring functions
