/*
*Function: transpose
*prints transpose
*
*array[][]: integer array that takes original array
*dimension: integer that stores dimension of array
*
*void type, doesn't return any value
*/
void transpose(int array[][10],int dimension)
{
    int temp;

    //iterating through the matrix to modify
    for(int i=0;i<dimension;i++)
    {
        for(int j=i;j<dimension;j++)
        {
            //swapping elements according to definition of transpose
            temp=array[j][i];
            array[j][i]=array[i][j];
            array[i][j]=temp;
        }
    }

    //printing the elements of transpose matrix
    for(int i=0;i<dimension;i++)
    {
        for(int j=0;j<dimension;j++)
        {
            printf("%d ",array[i][j]);
        }
        printf("\n");
    }
    return;
}

