int main()
{
    int n;
    //n is an integer variable used to store dimension of array

    int input_array[10][10];

    //reading value of n
    printf("Enter n: ");
    scanf("%d",&n);

    //reading values of 2-d array
    printf("Enter %d values: ",n*n);
    for(int i=0;i<n;i++)
    {
        for(int j=0;j<n;j++)
        {
            scanf("%d",&input_array[i][j]);
        }
    }

    //displaying input 2-d array
    printf("Input matrix: \n");
    for(int i=0;i<n;i++)
    {
        for(int j=0;j<n;j++)
        {
            printf("%d ",input_array[i][j]);
        }
        printf("\n");
    }

    if(isMagicSquare(input_array,n))
    {
        //if matrix is a magic square
        printf("\nEntered matrix is a Magic Square.\n");
    }
    else
    {
        //if matrix is not a magic square
        printf("\nEntered matrix is NOT a Magic Square.\n");
    }
    return 0;
}

/*
*Function: isMagicSquare
*returns whether the matrix is a Magic Square
*
*int array[][]: integer array that takes input array
*int dimension: integer variable that stores dimension of array
*
*returns: 1 if the matrix is a magic square, 0 if it's not
*/
int isMagicSquare(int array[][10],int dimension)
{
    int sum_diag_one=0, sum_diag_two=0;
    //sum_diag_one will store sum of elements of first diagonal
    //sum_diag_two will store sum of elements of second diagonal

    //finding sum of diagonal elements
    for (int i=0;i<dimension;i++)
    {
        sum_diag_one+=array[i][i];
        sum_diag_two+=array[i][dimension-1-i];
    }

    //if sum of diagonal elements are not equal, matrix can't be a magic square
    if(sum_diag_one!=sum_diag_two)
    {
        return 0;
    }

    //finding sum of rows and columns
    for(int i=0;i<dimension;i++)
    {
        int sum_row=0, sum_col=0;
        //sum_row will store sum of elements in a row
        //sum_col will store sum of elements in a column

        for(int j=0;j<dimension;j++)
        {
            sum_row+=array[i][j];
            sum_col+=array[j][i];
        }

        //if sum of row elements is not equal to sum of column elements then matrix can't be Magic Square
        //if sum of row elements and sum of column elements are not equal to sum of diagonal elements, then matrix can't be Magic Square
        if(sum_row!=sum_col || sum_row!=sum_diag_one)
        {
            return 0;
        }
    }

    //if all conditions are satisfied, then matrix is Magic Square. Hence, returning 1.
    return 1;
}

