    //if system is unable to allocate memory, then exit
    if  (pointer_to_pointer == NULL)
    {
		  printf ("\nMemory cannot be allocated");
		  exit(0);
	}

	//allocating memory for each row of 2-d array
	for(int i=0;i<n;i++)
    {
        pointer_to_pointer[i]=(float*)malloc(3*sizeof(float));

        //if system is unable to allocate memory, then exit
        if  (pointer_to_pointer[i] == NULL)
        {
            printf ("\nMemory cannot be allocated");
            exit(0);
        }
    }

    //allocating memory for 2-d array
    float **pointer_output_array;
    pointer_output_array=(float**)malloc(n*sizeof(float*));

    //if system is unable to allocate memory, then exit
    if  (pointer_output_array == NULL)
    {
        printf ("\nMemory cannot be allocated");
        exit(0);
    }

    //allocating memory for each row of 2-d array
    for(int i=0;i<n;i++)
    {
        pointer_output_array[i]=(float*)malloc(n*sizeof(float));

        //if system is unable to allocate memory, then exit
        if  (pointer_output_array[i] == NULL)
        {
            printf ("\nMemory cannot be allocated");
            exit(0);
        }
    }

    //reading n vectors
    printf("\nEnter %d vectors:\n",n);
    for(int i=0;i<n;i++)
    {
        for(int j=0;j<3;j++)
        {
            scanf("%f",&pointer_to_pointer[i][j]);
        }
    }

    //storing pairwise distances in nxn matrix
    for(int i=0;i<n;i++)
    {
        for(int j=0;j<n;j++)
        {
            //distance formula in 3-d space for every pair of vectors
            pointer_output_array[i][j]=sqrt(((pointer_to_pointer[i][0]-pointer_to_pointer[j][0])*(pointer_to_pointer[i][0]-pointer_to_pointer[j][0]))+((pointer_to_pointer[i][1]-pointer_to_pointer[j][1])*(pointer_to_pointer[i][1]-pointer_to_pointer[j][1]))+((pointer_to_pointer[i][2]-pointer_to_pointer[j][2])*(pointer_to_pointer[i][2]-pointer_to_pointer[j][2])));
        }
    }

    //printing the output matrix
    printf("\nOutput matrix is:\n");
    for(int i=0;i<n;i++)
    {
        for(int j=0;j<n;j++)
        {
            printf("%f\t",pointer_output_array[i][j]);
        }
        printf("\n");
    }

    //freeing the allocated memory
    for(int i=0;i<n;i++)
    {
        free(pointer_output_array[i]);
        free(pointer_to_pointer[i]);
    }
    free(pointer_output_array);
    free(pointer_to_pointer);

    return 0;
}

