struct student
{
    int roll;
    char firstname[20];
    char lastname[20];
    float cgpa;
    struct student *next;
}*slist, *tlist;

struct student * insert_asc(struct student *r, int t1, char t2[], char t3[], float t4)          //defining function to add student data in acending order
{
    struct student *p, *q, *nw;                                                                 //Defining temporary variables.
    nw = (struct student *)malloc(sizeof(struct student));                                      //Allocating space to temporary variable.

    nw->roll = t1;                                                                              //Copying the new data to a temporary structure of student.
    strcpy(nw->firstname, t2);
    strcpy(nw->lastname, t3);
    nw->cgpa = t4;
    nw->next = NULL;

    p = r;
    q = p;

    while(p!=NULL)                                                                          //Applying while loop to go through the list.
    {
        if(p->roll >= t1)                                                                       //If loop for ascending condition of rule number.
        {
            if(p == r)                                                                          //Condition. If only one element is present.
            {
                nw->next = r;
                return nw;
            }
            nw->next = p;                                                                   //Condition two add element at the middle of the linked list.
            q->next = nw;
            return r;
        }
        q = p;                                                                              //Condition to add element at last of the linked list.
        p = p->next;
    }
    if(r == NULL)return nw;
    else q->next = nw;
    return r;                                                                           //Returning the linked list after adding the element.
}

struct student * insert_asce(struct student *r, int t1, char t2[], char t3[], float t4)                 //Function to add data in order of Cgpa.
{
    struct student *p, *q, *nw;                                                                         //Defining temporary variables.

    nw = (struct student *)malloc(sizeof(struct student));                                          //Allocating space to temporary variables.

    nw->roll = t1;                                                                                  //Copying the new data to the temporary variable.
    strcpy(nw->firstname, t2);
    strcpy(nw->lastname, t3);
    nw->cgpa = t4;
    nw->next = NULL;

    p = r;
    q = p;

    while(p!=NULL)                                                                              //Applying while loop to go through the list.
    {
        if(p->cgpa > t4)                                                                        //Applying ascending condition to the SGP.
        {
            if(p == r)                                                                          //Condition. If only one element is already present.
            {
                nw->next = r;
                return nw;                                                                      //Returning the linked list.
            }
            nw->next = p;                                                                       //Condition to add a element between the linked list.
            q->next = nw;
            return r;                                                                            //Returning the linked list.
        }

        q = p;                                                                                  //Switching to the next element.
        p = p->next;
    }
    if(r == NULL)return nw;                                                                 //Adding element at the last.
    else q->next = nw;
    return r;                                                                                   //Returning the linked list.
}

struct student *deletroll(struct student *r, int t1)                                                  //Function to delete a element in the linked list.
{

    struct student *p, *q;                                                                          //Defining temporary variables.
    p = r;
    q = p;

    while(p!=NULL)                                                                                      //Applying while loop to go through the list.
    {
        if(p->roll == t1)                                                                               //Condition to check the roll number.
        {
            if(p == r)r = p->next;                                                                      //Condition. If only one element is present.
            else q->next = p->next;                                                                     //Condition. If more than one elements are present.
            p->next = NULL;
            free(p);                                                                                    //Freeing up space if element is found.
            return r;                                                                                   //Returning the updated linked list.
        }
        else
        {
            q = p;                                                                                      //Moving to the next element, if Rule number is not present.
            p = p->next;
        }
    }

    return r;                                                                                       //Returning the linked list.
}

