#include<stdio.h>
#include<string.h>
#include<stdlib.h>

typedef struct node {
    int data;
    struct node* next;
} node;

typedef node *linkedlist;

// initializing the linked list with head that stores NULL
void init(linkedlist* l){
    linkedlist head = (linkedlist) malloc(sizeof(linkedlist));
    head = NULL;
    (*l) = head;
}

// displays the linked list from head till the last element
void display(linkedlist l){
    linkedlist ptr;
    ptr = l;
    printf("( ");
    // goes on printing the data till ptr becomes NULL
    while(ptr != NULL){
        printf("%d ", ptr->data);
        ptr = ptr->next;
    }
    printf(")\n");
}

// used to insert an element at the front of the linked list
void insert_front(linkedlist* l, int x){
    // newNode stores the new value entered by the user
    linkedlist newNode = (linkedlist) malloc(sizeof(linkedlist));
    // head stores the head of the linked list
    linkedlist head = *l;
    newNode->data = x;
    // newNode is made to point to the head of the linked list
    newNode->next = head;
    // head of the linked list is now made the newNode
    (*l) = newNode;
    // modified linked list is displayed
    display(*l);
}

// used to delete an element at the front of the linked list
void delete_front(linkedlist* l){
    // we can delete an element if the linked list is not empty and so we check that first
    if((*l) != NULL){
        // node to be deleted which is the head is made to store in deleteNode
        linkedlist deleteNode = (*l);
        // head is moved one element forward
        (*l) = (*l)->next;
        // node to be deleted is freed from memory
        free(deleteNode);
    }
    display(*l);
}

// reverses the linked list
void reverse(linkedlist* l){
    // no work to be done is the linked list is empty
    if(*l == NULL){
        printf("( )\n");
        return;
    }
    // prev stores the previous element in the current linked list and initially it is NULL as to the left of head there is nothing
    // current traverses through the linked list
    // nextNode stores the next element in the current linked list
    // here we reverse the links between the elements of the linked list
    linkedlist prev, current, nextNode;
    prev = NULL;
    current  = *l;
    while(current != NULL){
        nextNode = current->next;
        current->next = prev;
        prev = current;
        current = nextNode;
    }
    // head is made to point to the end of the original linked list
    *l = prev;
    display(*l);
}

