        if(p==NULL)
        {
            printf ("\nMemory cannot be allocated");
            exit(0);
        }

        //reversing the linked list
        if(prev==NULL) p=reversed;
        p->data=h->data;
        p->next=prev;
        prev=p;
        h=h->next;
        if(h==NULL) reversed=p; //if we reach end of original list, then pointer pointing to this value will be header for reversed list
    }

    return reversed;    //returning the result
}

