        printf("Done deleting extra player records\n");
    }else{
        printf("The list already contains this many players\n");
    }
    return r;
}

//this function counts required ODIs more than or equal to n
int one_day_counter(int n, struct Player* node, int odi){
    if(node!=NULL){
        if(node->n_odi >= n){
            return(one_day_counter(n, node->next, odi+1)); //recursive call with odi+1
        }
        return(one_day_counter(n, node->next, odi));
    }
    return odi;
}

//this function calls the previous one and just prints the value
void one_day_printer(int n, struct Player* r){
    struct Player* p = r;
    printf("The number of players who have played atleast %d one day internationals are: %d\n", n, one_day_counter(n,p,0));
}

