#include<stdio.h>
#include<string.h>
#include<stdlib.h>

#define SIZE 50

// structure definition as given in question
typedef struct node {
    char first_name[SIZE];
    char last_name[SIZE];
    struct node *next;
} node;
typedef node *linkedlist;

// init() function initialises the linked list with a head node which is given the value NULL and returned
linkedlist init(){
    linkedlist head = (linkedlist)malloc(sizeof(node));
    head = NULL;
    return head;
}

// display() function displays the linked list
void display(linkedlist l){
    linkedlist p = l;
    printf("Linked List: (");
    while(p!=NULL){
        printf("%s %s,", p->first_name, p->last_name);
        p = p->next;
    }
    printf(")\n");
}

// insert_middle() function inserts a given element in the middle of the linked list as told in the question
linkedlist insert_middle(linkedlist l, char first[], char last[]){
    // p is initialized to l and this p is later used extensively in the code
    linkedlist p = l;
    // middle node is created with the help of malloc()
    linkedlist middle = (linkedlist)malloc(sizeof(node));
    // middle node is initialized with the parameters obtained from the main
    strcpy(middle->first_name, first);
    strcpy(middle->last_name, last);
    // condition when the linked list is empty
    if(p==NULL){
        middle->next = NULL;
        return middle;
    }
    int size = 0;
    // size of the linked list is determined so that the middle node can be inserted at the right place
    while(p!=NULL){
        size++;
        p = p->next;
    }
    p = l;
    // special case when size is one and then the middle node is inserted to the left of p and returned as head node
    if(size==1){
        middle->next = p;
        return middle;
    }
    int reqd = 1;
    // here the proper position is found where the middle node is to be inserted for both cases of even and odd size
    if(size%2==1){
        while(reqd!=(size-1)/2){
            reqd++;
            p = p->next;
        }
        // links are made and broken
        middle->next = p->next;
        p->next = middle;
    }else{
        while(reqd!=(size)/2){
            reqd++;
            p = p->next;
        }
        // links are made and broken
        middle->next = p->next;
        p->next = middle;
    }
    // head is returned
    return l;
}

// delete_middle() function deletes the middle node of the linked list as told in the question
linkedlist delete_middle(linkedlist l){
    // p is initialized to l and this p is later used extensively in the code
    linkedlist p = l;
    // prev keeps track of the node prior to l
    linkedlist prev;
    // when linked list is empty then we just return back the head
    if(p==NULL){
        return p;
    }
    int size = 0;
    // size of the linked list is determined so that the middle node can be founed and deleted
    while(p!=NULL){
        size++;
        p = p->next;
    }
    p = l;
    // special case when size is one and then the only node present is deleted and NULL is returned indicating that the linked list is empty
    if(size==1){
        free(p);
        return NULL;
    }
    // special case when size is two and so the left node is deleted and right node is made head and returned
    if(size==2){
        l = p->next;
        free(p);
        return l;
    }
    int reqd = 1;
    // here the proper position is found from where the middle node is to be deleted for both cases of even and odd size
    if(size%2==1){
        while(reqd!=(size+1)/2){
            reqd++;
            prev = p;
            p = p->next;
        }
        // new links are made and middle node is deleted
        prev->next = p->next;
        free(p);
    }else{
        prev = p;
        while(reqd!=(size)/2){
            reqd++;
            prev = p;
            p = p->next;
        }
        // new links are made and middle node is deleted
        prev->next = p->next;
        free(p);
    }
    return l;
}

// sort() functions sorts the linked list according to Bubble Sort as the last digit
// of my roll number is 9 and so we get a remainder of 0 when divided by 3
void sort(linkedlist* l, int key){
    linkedlist* p = l;
    //head node is stored in head
    linkedlist head = *(l);
    int size = 0;
    //size of linked list is found which is to be used to determine the number of iterations in Bubble Sort
    while(*p != NULL){
        size++;
        *p = (*p)->next;
    }
    // p stores the address of the head
    (*p) = head;
    // different logical snippets according to the value of key
    // only the first snippet is commented as the rest are very much similar
    // sort in non-decreasing order of first name
    if(key==0){
        // usual Bubble sort loops
        for(int i=0; i<size-1; i++){
            for(int j=0; j<size-i-1; j++){
                //changing the names from upperCase to lowerCase for comparison but the original names are only swapped
                char name1[SIZE];
                char name2[SIZE];
                int k = 0;
                while((*p)->first_name[k]!='\0'){
                    if((*p)->first_name[k] >='A' && (*p)->first_name[k]<='Z'){
                        name1[k] = (*p)->first_name[k]-'A'+'a';
                    }else{
                        name1[k] = (*p)->first_name[k];
                    }
                    k++;
                }
                name1[k] = '\0';
                k = 0;
                while((*p)->next->first_name[k]!='\0'){
                    if((*p)->next->first_name[k] >='A' && (*p)->next->first_name[k]<='Z'){
                        name2[k] = (*p)->next->first_name[k]-'A'+'a';
                    }else{
                        name2[k] = (*p)->next->first_name[k];
                    }
                    k++;
                }
                name2[k] = '\0';
                // strcmp() is a string.h function which compares the two names
                if(strcmp(name1, name2)>0){
                    // first and last names are swapped
                    // temp stores the names to be sorted temporarily
                    char temp[SIZE];
                    strcpy(temp, (*p)->first_name);
                    strcpy((*p)->first_name, (*p)->next->first_name);
                    strcpy((*p)->next->first_name, temp);

                    strcpy(temp, (*p)->last_name);
                    strcpy((*p)->last_name, (*p)->next->last_name);
                    strcpy((*p)->next->last_name, temp);
                }
                // next node address is stored in p
                *p = (*p)->next;
            }
            (*p) = head;
        }
    }
    // sort in non-increasing order of first name
    else if(key==1){
        for(int i=0; i<size-1; i++){
            for(int j=0; j<size-i-1; j++){
                //changing the names from upperCase to lowerCase for comparison but the original names are only swapped
                char name1[SIZE];
