                        name2[k] = (*p)->next->first_name[k]-'A'+'a';
                    }else{
                        name2[k] = (*p)->next->first_name[k];
                    }
                    k++;
                }
                name2[k] = '\0';
                if(strcmp(name1, name2)<0){
                    char temp[SIZE];
                    strcpy(temp, (*p)->first_name);
                    strcpy((*p)->first_name, (*p)->next->first_name);
                    strcpy((*p)->next->first_name, temp);

                    strcpy(temp, (*p)->last_name);
                    strcpy((*p)->last_name, (*p)->next->last_name);
                    strcpy((*p)->next->last_name, temp);
                }
                *p = (*p)->next;
            }
            (*p) = head;
        }
    }
    // sort in non-decreasing order of last name
    else if(key==2){
        for(int i=0; i<size-1; i++){
            for(int j=0; j<size-i-1; j++){
                //changing the names from upperCase to lowerCase for comparison but the original names are only swapped
                char name1[SIZE];
                char name2[SIZE];
                int k = 0;
                while((*p)->last_name[k]!='\0'){
                    if((*p)->last_name[k] >='A' && (*p)->last_name[k]<='Z'){
                        name1[k] = (*p)->last_name[k]-'A'+'a';
                    }else{
                        name1[k] = (*p)->last_name[k];
                    }
                    k++;
                }
                name1[k] = '\0';
                k = 0;
                while((*p)->next->last_name[k]!='\0'){
                    if((*p)->next->last_name[k] >='A' && (*p)->next->last_name[k]<='Z'){
                        name2[k] = (*p)->next->last_name[k]-'A'+'a';
                    }else{
                        name2[k] = (*p)->next->last_name[k];
                    }
                    k++;
                }
                name2[k] = '\0';
                if(strcmp(name1, name2)>0){
                    char temp[SIZE];
                    strcpy(temp, (*p)->first_name);
                    strcpy((*p)->first_name, (*p)->next->first_name);
